import { Row, Col, Form, Input, Select, Button, Space } from 'antd';
import { useEffect, useState } from 'react';
import Post from '../components/Post';
import HttpRequest from '../utils/HttpRequest';
const { Option } = Select;

function Posts() {
  const [form] = Form.useForm();

  const postsData = [
    {
      id: '1',
      title: 'My first post',
      description: 'My first description',
      menu: 'Todays menu!',
    },
    {
      id: '2',
      title: 'My Second post',
      description: 'My second description',
      menu: 'Todays menu!',
    },
  ];

  const menus = [
    {
      id: '1',
      title: 'My first menu',
      content: 'Pizza',
    },
    {
      id: '2',
      title: 'My Second menu',
      content: 'Ham',
    },
  ];

  const [posts, setPosts] = useState(null);

  const handlePublish = () => {
    setPosts([...posts, { id: Math.random(), ...form.getFieldsValue() }]);
    console.log(posts);
  };

  useEffect(() => {
    const getPosts = async () => {
      await HttpRequest.GET(
        '/api/v1/restaurant/posts',
        localStorage.getItem('access_token')
      );
    };
    setPosts(getPosts());
  }, []);

  return (
    <>
      <Row gutter={16} style={{ marginTop: '2em' }}>
        <Col span={6} offset={9}>
          <Form
            form={form}
            name="normal_login"
            initialValues={{
              remember: true,
            }}
          >
            <Form.Item name="title" label="Post title">
              <Input placeholder="Enter post title" />
            </Form.Item>
            <Form.Item name="description" label="Post description">
              <Input.TextArea
                type="textArea"
                placeholder="Enter post description"
              />
            </Form.Item>
            <Form.Item name="menu" label="Post menu">
              <Select placeholder="Select menu" allowClear>
                {menus.map((menu) => (
                  <Option key={menu.id} value={menu.id}>
                    {menu.title}
                  </Option>
                ))}
              </Select>
            </Form.Item>
            <Form.Item>
              <Col offset={5}>
                <Space size="large" split={true}>
                  <Button
                    type="primary"
                    htmlType="submit"
                    onClick={handlePublish}
                  >
                    Publish post
                  </Button>
                  <Button type="primary" onClick={() => form.resetFields()}>
                    Clear form
                  </Button>
                </Space>
              </Col>
            </Form.Item>
          </Form>
        </Col>
      </Row>
      <Row gutter={16} style={{ marginTop: '2em' }}>
        <Col span={6} offset={9}>
          {posts.map((post) => (
            <Post
              key={post.id}
              title={post.title}
              description={post.description}
              menu={post.menu}
            />
          ))}
        </Col>
      </Row>
    </>
  );
}

export default Posts;
