import { Row, Col, Form, Input, Button, Space } from 'antd';
import { useForm } from 'antd/lib/form/Form';
import { useEffect, useState } from 'react';
import Menu from '../components/Menu';
import { useDataContext } from '../hooks/useDataContext';

function Menus() {
  const [form] = useForm();
  const { menus, fetchMenus, addMenu } = useDataContext();
  const [changeFlag, setChangeFlag] = useState(true);

  useEffect(() => {
    const abortController = new AbortController();

    const loadMenus = async () => await fetchMenus();
    loadMenus();

    return () => {
      abortController.abort();
    };
  }, [changeFlag]);

  const handleAddMenu = async () => {
    // await form.validateFields();
    const validate = await form.validateFields();
    console.log(validate);
    await addMenu(await form.getFieldsValue());
    form.submit();
    form.resetFields();
    setChangeFlag(!changeFlag);
  };
  return (
    <>
      <Row gutter={16} style={{ marginTop: '2em' }}>
        <Col span={6} offset={9}>
          <Form
            form={form}
            name="normal_login"
            initialValues={{
              remember: true,
            }}
          >
            <Form.Item
              name="title"
              label="Post title"
              rules={[
                {
                  required: true,
                  message: 'Please enter title',
                },
              ]}
            >
              <Input placeholder="Enter menu title" />
            </Form.Item>
            <Form.Item
              name="content"
              label="Menu content"
              rules={[
                {
                  required: true,
                  message: 'Please enter menu contet',
                },
              ]}
            >
              <Input.TextArea
                type="textArea"
                placeholder="Enter post description"
              />
            </Form.Item>
            <Form.Item>
              <Col offset={5}>
                <Space size="large" split={true}>
                  <Button
                    type="primary"
                    htmlType="submit"
                    onClick={handleAddMenu}
                  >
                    Add menu
                  </Button>
                  <Button type="primary" onClick={() => form.resetFields()}>
                    Clear form
                  </Button>
                </Space>
              </Col>
            </Form.Item>
          </Form>
        </Col>
      </Row>
      <Row gutter={16} style={{ marginTop: '2em' }}>
        <Col span={6} offset={9}>
          {menus &&
            menus.map((menu) => (
              <Menu key={menu.id} title={menu.title} content={menu.content} />
            ))}
        </Col>
      </Row>
    </>
  );
}

export default Menus;
