import { Row, Col, Form, Input, Button, Space } from 'antd';
import { useForm } from 'antd/lib/form/Form';
import Menu from '../components/Menu';

function Menus() {
  const [form] = useForm();
  const menus = [
    {
      id: '1',
      title: 'My first menu',
      content: 'Pizza',
    },
    {
      id: '2',
      title: 'My Second menu',
      content: 'Ham',
    },
  ];
  return (
    <>
      <Row gutter={16} style={{ marginTop: '2em' }}>
        <Col span={6} offset={9}>
          <Form
            form={form}
            name="normal_login"
            initialValues={{
              remember: true,
            }}
          >
            <Form.Item name="title" label="Post title">
              <Input placeholder="Enter menu title" />
            </Form.Item>
            <Form.Item name="content" label="Menu content">
              <Input.TextArea
                type="textArea"
                placeholder="Enter post description"
              />
            </Form.Item>
            <Form.Item>
              <Col offset={5}>
                <Space size="large" split={true}>
                  <Button
                    type="primary"
                    htmlType="submit"
                    // onClick={handlePublish}
                  >
                    Add menu
                  </Button>
                  <Button type="primary" onClick={() => form.resetFields()}>
                    Clear form
                  </Button>
                </Space>
              </Col>
            </Form.Item>
          </Form>
        </Col>
      </Row>
      <Row gutter={16} style={{ marginTop: '2em' }}>
        <Col span={6} offset={9}>
          {menus.map((menu) => (
            <Menu key={menu.id} title={menu.title} content={menu.content} />
          ))}
        </Col>
      </Row>
    </>
  );
}

export default Menus;
