import { Row, Col, Button, Layout, List, Card } from 'antd';
import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const { Header, Footer, Content } = Layout;

function PublicPostView() {
  const navigate = useNavigate();
  const { postId } = useParams();
  const [selectedPost, setSelectedPost] = useState(null);
  useEffect(() => {
    const getPostById = async () => {
      const res = await fetch(`/api/v1/restaurant/${postId}`);
      const data = await res.json();
      setSelectedPost({ ...data });
    };
  }, []);

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Header>
        <Row style={{ width: '100%' }}>
          <Col offset={3}>
            <h1 className="logo" onClick={() => navigate('/home')}>
              PorscheRestaurant
            </h1>
          </Col>
          <Col offset={16}>
            <Button type="danger" onClick={() => navigate('/login')}>
              APP
            </Button>
          </Col>
        </Row>
      </Header>
      <Content
        style={{ height: '80vh', overflow: 'hidden', margin: '2em 0 0 0' }}
      >
        <Row>
          <Col offset={4} span={16}>
            <Card>{selectedPost && selectedPost.title}</Card>
          </Col>
        </Row>
      </Content>
      <Footer style={{ position: 'sticky', bottom: '0' }}>
        <Row>
          <Col offset={12}>
            <h4
              style={{ cursor: 'pointer' }}
              onClick={() =>
                (window.location.href = 'https://www.porsche.digital/en/')
              }
            >
              www.porsche.digital
            </h4>
          </Col>
        </Row>
      </Footer>
    </Layout>
  );
}

export default PublicPostView;
