import { Row, Col, Form, Input, Select, Button, Space } from 'antd';
import Post from '../components/Post';
const { Option } = Select;

function Posts() {
  const [form] = Form.useForm();
  const posts = [
    {
      id: '1',
      title: 'My first post',
      description: 'My first description',
      menu: 'Todays menu!',
    },
    {
      id: '2',
      title: 'My Second post',
      description: 'My second description',
      menu: 'Todays menu!',
    },
  ];

  const menus = [
    {
      id: '1',
      title: 'My first menu',
      content: 'Pizza',
    },
    {
      id: '2',
      title: 'My Second menu',
      content: 'Ham',
    },
  ];
  return (
    <Row gutter={16} style={{ marginTop: '2em' }}>
      <Col span={6} offset={3}>
        <Form
          form={form}
          name="normal_login"
          initialValues={{
            remember: true,
          }}
        >
          <Form.Item name="title" label="Post title">
            <Input placeholder="Enter post title" />
          </Form.Item>
          <Form.Item name="description" label="Post description">
            <Input.TextArea
              type="textArea"
              placeholder="Enter post description"
            />
          </Form.Item>
          <Form.Item name="menu" label="Post menu">
            <Select placeholder="Select menu" allowClear>
              {menus.map((menu) => (
                <Option key={menu.id} value={menu.id}>
                  {menu.title}
                </Option>
              ))}
            </Select>
          </Form.Item>
          <Form.Item>
            <Space align="center">
              <Button type="primary" htmlType="submit">
                Publish post
              </Button>
              <Button type="primary" htmlType="submit">
                Publish post
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Col>
      <Col offset={4} span={6}>
        {posts.map((post) => (
          <Post
            key={post.id}
            title={post.title}
            description={post.description}
          />
        ))}
      </Col>
    </Row>
  );
}

export default Posts;
